mapper.update:

update user set
user_profile_image_url = #{userProfileImageUrl}
where user_email = #{userEmail}

update user set
user_name = #{userName},
user_password = #{userPassword},
user_rank = #{userRank},
user_message = #{userMessage}
where user_email = #{userEmail}

mapper.select:
select *
from user 
where user_email = #{userEmail} and user_name = #{userName}
